settings.custom_css:
selector .elementor-image-box-wrapper {
    transition: transform 300ms ease 0ms,filter 300ms ease 0ms;
}
selector .elementor-image-box-img {
    display: flex;
}
selector .elementor-image-box-img img {
    width:100%;
}
selector .elementor-image-box-content {
    display: none;
    padding:10px 20px; 
    background:#FFB302;
    transition: transform 300ms ease 0ms,filter 300ms ease 0ms;
}
selector .elementor-image-box-wrapper:hover {
    transform:scaleX(1.1) scaleY(1.1)!important;
}
selector .elementor-image-box-wrapper:hover .elementor-image-box-content {
    display: block;

}

